import React,{useContext, useState} from 'react';
import Modal from 'react-modal'

import { TagContext } from '../TagContext'

import Tag from './Tag/Tag'
import ikeaImage from '../images/ikeaImage.jpg'

import useStyles from './styles'


function IkeaImage() {
  const classes = useStyles();

  const [query,setQuery] = useContext(TagContext);
  const [isOpenTable,setIsOpenTable] = useState(false)
  const [isOpenCouch,setIsOpenCouch] = useState(false)
  const [isOpenPortrait,setIsOpenPortrait] = useState(false)
  const [isOpenWatch,setIsOpenWatch] = useState(false)

  const handleShowModals = () =>{
    if(query.name==='EKERO'){
      setIsOpenTable(true)
      setIsOpenCouch(false)
      setIsOpenPortrait(false)
      setIsOpenWatch(false)
    }
    else if(query.name==='SODERHANN')
    {
      setIsOpenTable(false)
      setIsOpenCouch(true)
      setIsOpenPortrait(false)
      setIsOpenWatch(false)
    }
    else if(query.name==='PORTRAIT')
    {
      setIsOpenTable(false)
      setIsOpenCouch(false)
      setIsOpenPortrait(true)
      setIsOpenWatch(false)
    }
    else if(query.name==='ROLEX')
    {
      setIsOpenTable(false)
      setIsOpenCouch(false)
      setIsOpenPortrait(false)
      setIsOpenWatch(true)
    }
  }

  return (
      <div className={classes.container}>
        <img className={classes.image} src={ikeaImage} alt='Living Room'/>
        <button className={classes.btn}>Button</button>
        <button className={classes.btn}>Button</button>
        <button className={classes.btn}>Button</button>
        <button className={classes.btn}>Button</button>
        <Modal>
        <Tag name={query.name} price={query.price}/>
        </Modal>
        <Modal>
        <Tag name={query.name} price={query.price}/>
        </Modal>
        <Modal>
        <Tag name={query.name} price={query.price}/>
        </Modal>
        <Modal>
        <Tag name={query.name} price={query.price}/>
        </Modal>
      </div>
  );
}

export default IkeaImage;
