import {makeStyles} from '@material-ui/core/styles'

export default makeStyles(()=>({
    container:{
        position: 'relative',
        width: '50%'
    },
    image:{
        width: '100%',
        height: 'auto'
    },
    btn:{
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        msTransform: 'translate(-50%, -50%)',
        backgroundColor: '#555',
        color: 'white',
        fontSize: '16px',
        padding: '12px 24px',
        border: 'none',
        cursor: 'pointer',
        borderRadius: '5px'
    }

}));
