import React,{useContext, useState} from 'react';

import { TagContext } from '../TagContext'

import Tag from './Tag/Tag'
import ikeaImage from '../images/ikeaImage.jpg'

import useStyles from './styles'


function IkeaImage() {
  const classes = useStyles();

  const [query,setQuery] = useContext(TagContext);

  const [showHideObjects,setShowHideObjectes]= useState({
                 showTable: false,
                 showCouch: false,
                 showPortrait: false,
                 showWatch: false,
              })
  const handleShowHideTable = () =>{
      setShowHideObjectes({
                 showTable: true,
                 showCouch: false,
                 showPortrait: false,
                 showWatch: false,})    
  }
  const handleShowHideCouch = () =>{
      setShowHideObjectes({
                showTable: false,
                showCouch: true,
                showPortrait: false,
                showWatch: false,})    
}
  const handleShowHidePortrait = () =>{
     setShowHideObjectes({
               showTable: false,
               showCouch: false,
               showPortrait: true,
               showWatch: false,})    
}
  const handleShowHideWatch = () =>{
     setShowHideObjectes({
              showTable: false,
              showCouch: false,
              showPortrait: false,
              showWatch: true,})    
}





  return (
      <div className={classes.container}>
        <img className={classes.image} src={ikeaImage} alt='Living Room'/>
        <button className={classes.btn}>Button</button>
        <Tag name={query.name} price={query.price}/>
      </div>
  );
}

export default IkeaImage;
