import React,{useState,useContext} from 'react';

import { TagContext } from '../TagContext'

import { AiOutlineSearch } from 'react-icons/ai';


import useStyles from './styles'




function SearchBox() {
  const classes = useStyles();

  const [tags,setTags] = useState([
    {
        name:'EKERO',
        price:'€220',
        title:'table'

    },
    {
        name:'SODERHANN',
        price:'€450',
        title:'couch'
    },
    {
        name:'PORTRAIT',
        price:'€180',
        title:'portrait'
    },
    {
        name:'ROLEX',
        price:'€110',
        title:'watch'
    },

  ]);
  const [search,setSearch] = useState("");
  const [query,setQuery] = useContext(TagContext);

  const updateSearch = e =>{
    setSearch(e.target.value);
    console.log(search)
  };

//CREATE AN OBJECT DEPENDING ON SEARCH
  const getSearch = e => {
    e.preventDefault();
    tags.map((tag)=>{
      if(search===tag.name){
        setQuery({ name: tag.name,
                   price: tag.price,
                  })
        setSearch("")
      }
      else{
        setSearch("")
      }
    })      
  }

  return (
    <div>
      <form className={classes.form} onSubmit={getSearch}>
        <input className={classes.searchBox} 
               type='text' 
               placeholder='Select Item' 
               value={search}
               onChange={updateSearch}

               />
        <button className={classes.btn} type='submit'><AiOutlineSearch /></button>
      </form>
    </div>

  );
}

export default SearchBox;
