import React,{useContext, useEffect, useState} from 'react';
import Modal from 'react-modal'

import { TagContext } from '../TagContext'

import Tag from './Tag/Tag'
import ikeaImage from '../images/ikeaImage.jpg'

import useStyles from './styles'
import { BiChevronUp } from 'react-icons/bi';



function IkeaImage() {
  const classes = useStyles();

  const [query,setQuery] = useContext(TagContext);
  const [isOpenTable,setIsOpenTable] = useState(false)
  const [isOpenCouch,setIsOpenCouch] = useState(false)
  const [isOpenPortrait,setIsOpenPortrait] = useState(false)
  const [isOpenWatch,setIsOpenWatch] = useState(false)

  const changeQueryOnClickTable= () =>{
    setQuery({ name: 'EKERO',
              price: '€220',
     })
  }

  const handleShowModals = () =>{
    if(query.name==='EKERO'){
      setIsOpenTable(true)   
    }
    else if(query.name==='SODERHANN')
    {
      setIsOpenCouch(true)
    }
    else if(query.name==='PORTRAIT')
    {  
      setIsOpenPortrait(true)
    }
    else if(query.name==='ROLEX')
    {
      setIsOpenWatch(true)
    }
  }

  useEffect(()=>{
    handleShowModals();
  },[query]);

  return (
      <div className={classes.container}>
        <img className={classes.image} src={ikeaImage} alt='Living Room'/>
        <button className={classes.tableButton} onClick={changeQueryOnClickTable}><BiChevronUp /></button>
        <button className={classes.couchButton} onClick={()=>setIsOpenCouch(true)}><BiChevronUp /></button>
        <button className={classes.portraitButton} onClick={()=>setIsOpenPortrait(true)}><BiChevronUp /></button>
        <button className={classes.watchButton} onClick={()=>setIsOpenWatch(true)}><BiChevronUp /></button>

        <Modal className={classes.modal} isOpen={isOpenTable} onRequestClose={()=>setIsOpenTable(false)}>
          <Tag name={query.name} price={query.price}/>
        </Modal>
        <Modal isOpen={isOpenCouch} onRequestClose={()=>setIsOpenCouch(false)}>
          <Tag name={query.name} price={query.price}/>
        </Modal>
        <Modal isOpen={isOpenPortrait} onRequestClose={()=>setIsOpenPortrait(false)}>
          <Tag name={query.name} price={query.price}/>
        </Modal>
        <Modal isOpen={isOpenWatch} onRequestClose={()=>setIsOpenWatch(false)}>
          <Tag name={query.name} price={query.price}/>
        </Modal>
      </div>
  );
}

export default IkeaImage;
